<template>
  <div class="container">
    <div v-for="a of answer" :key="a.id" class="tile" @click="onClick(a.id)">
      <div v-if="a.open">
      <img v-bind:src="a.image" >
      </div>
      <div v-else>&nbsp;</div>
    </div>
  </div>
</template>

<script>
import { v4 as uuidv4 } from "uuid";
const answer = [
	{ name: "doraemon", image: "https://www.brandbuffet.in.th/wp-content/uploads/2020/08/Doraemon-Cartoon-Character.jpg" },
	{ name: "stitch", image: "data:image/png;base64," },
	{ name: "pooh", image: "https://www.pngkit.com/png/detail/41-412209_winnie-the-pooh-cute-pose-winnie-the-pooh.png" },
	{ name: "piglet", image: "https://i.pinimg.com/originals/92/73/d4/9273d439e60aafc514863a91a80ceda6.jpg" },
	{ name: "rabbit", image: "https://img.freepik.com/free-vector/happy-cute-rabbit-with-duck-jumping-having-fun-cartoon-illustration_56104-472.jpg?w=2000" },
	{ name: "panda", image: "https://static.vecteezy.com/system/resources/previews/004/226/762/original/panda-cartoon-cute-say-hello-panda-animals-illustration-vector.jpg" },
  { name: "doraemon", image: "https://www.brandbuffet.in.th/wp-content/uploads/2020/08/Doraemon-Cartoon-Character.jpg" },
	{ name: "stitch", image: "data:image/png;base64," },
	{ name: "pooh", image: "https://www.pngkit.com/png/detail/41-412209_winnie-the-pooh-cute-pose-winnie-the-pooh.png" },
	{ name: "piglet", image: "https://i.pinimg.com/originals/92/73/d4/9273d439e60aafc514863a91a80ceda6.jpg" },
	{ name: "rabbit", image: "https://img.freepik.com/free-vector/happy-cute-rabbit-with-duck-jumping-having-fun-cartoon-illustration_56104-472.jpg?w=2000" },
	{ name: "panda", image: "https://static.vecteezy.com/system/resources/previews/004/226/762/original/panda-cartoon-cute-say-hello-panda-animals-illustration-vector.jpg" }
]
  .map((n) => {
    return {
      id: uuidv4(),
      open: false,
      value: n.name,
      image : n.image
    };
  })
  .sort(() => Math.random() - 0.5);

export default {
  name: "HelloWorld",
  data() {
    return {
      answer,
      itemIds: []
    };
  },
  methods: {
    onClick(id) {
      if (!this.itemIds.length < 2 && !this.itemIds.includes(id)) {
        this.itemIds.push(id);
      }
      const index = this.answer.findIndex((a) => a.id === id);
      this.answer[index].open = true;
      if (this.itemIds.length === 2) {
        const item1Index = this.answer.findIndex(
          (a) => a.id === this.itemIds[0]
        );
        const item2Index = this.answer.findIndex(
          (a) => a.id === this.itemIds[1]
        );
        if (this.answer[item1Index].value !== this.answer[item2Index].value) {
          this.answer[item1Index].open = false;
          this.answer[item2Index].open = false;
        }
      }
      if (this.itemIds.length === 2) {
        this.itemIds = [];
      }
    },
  },
};
</script>

<style scoped>
.container {
  display: flex;
  grid-template-columns: 85px 85px 85px;
  grid-template-rows: 68px 68px 68px 68px;
  grid-gap: 5px;
  justify-content: center;
  margin-top: 20px;
}

.tile {
  border: 1px solid black;
  width: 200vw;
  height: 150px;
}

img {
  width: 100px;
  height: 100px;
}
</style>